    public Carte() {
        try {
            BufferedImage tileset = ImageIO.read(getClass().getResource("images/tilesetv2.png"));
            BufferedImage tileset2 = ImageIO.read(getClass().getResource("images/background.png"));
            tuiles = new BufferedImage[120];
            for (int i = 0; i < tuiles.length; i++){
                int x = (i % 24) * tailleTuile;
                int y = (i / 24) * tailleTuile;
                tuiles[i] = tileset.getSubimage(x, y, tailleTuile , tailleTuile);
            }   
            tuiles2 = new BufferedImage[3];
            for (int i = 0; i < 3; i++){
                int x = (i%1)* tailleTuile3;
                int y = (i / 1) * tailleTuile3;
                tuiles2[i] = tileset2.getSubimage(x, y, tailleTuile2 , tailleTuile3);
            }
        } catch (IOException ex) {
            Logger.getLogger(Carte.class.getName()).log(Level.SEVERE, null, ex);
        }
    }

